import React, { useEffect, useState } from 'react';
import { fetchImageList } from '../utils/imageFetcher';

const latest_image = () => {
  const [images, setImages] = useState([]);
  const [analyzedImages, setAnalyzedImages] = useState([]);

  useEffect(() => {
    fetchImageList()
      .then(data => {
        console.log("‚úÖ Î∞õÏïÑÏò® ÏõêÎ≥∏ Ïù¥ÎØ∏ÏßÄÎì§:", data);
        setImages(data);
      })
      .catch(err => {
        console.error("‚ùå ÏõêÎ≥∏ Ïù¥ÎØ∏ÏßÄ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
      });

    fetch('http://220.149.235.221:4000/api/images_result/')
      .then(res => res.json())
      .then(fileList => {
        const sorted = fileList
          .filter(name => name.endsWith('.jpg'))
          .sort((a, b) => {
            const getTime = f => {
              const match = f.match(/frame_(\d+)\.jpg/);
              return match ? parseInt(match[1]) : 0;
            };
            return getTime(b) - getTime(a);
          });

        const fullData = sorted.map(name => ({
          name,
          url: `http://220.149.235.221:4000/images_result/${name}`,
          timestamp: parseInt(name.match(/frame_(\d+)\.jpg/)?.[1] || 0)
        }));

        console.log("‚úÖ Î∞õÏïÑÏò® AI Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄÎì§:", fullData);
        setAnalyzedImages(fullData);
      })
      .catch(err => {
        console.error("‚ùå Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
      });
  }, []);

  const formatDate = (ts) => {
    const date = new Date(ts);
    return date.toLocaleString('ko-KR');
  };

  return (
    <div className="p-6 max-w-screen-xl mx-auto">
      <h1 className="text-2xl font-bold mb-6">Ï†ÑÏ≤¥ Ï†ÄÏû•Îêú Ïù¥ÎØ∏ÏßÄ Î™©Î°ù</h1>

      {/* ÏõêÎ≥∏ Ïù¥ÎØ∏ÏßÄ ÏÑπÏÖò */}
      <h2 className="text-xl font-semibold mb-4">üì∑ ÏõêÎ≥∏ Ïù¥ÎØ∏ÏßÄ</h2>
      <div className="grid grid-cols-3 gap-6 mb-12">
        {images.length === 0 ? (
          <div className="text-gray-500">Î∂àÎü¨Ïò¨ Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</div>
        ) : (
          images.map((img, idx) => (
            <div key={idx} className="border p-2 text-center space-y-2">
              <div className="h-48 flex items-center justify-center border">
                <img
                  src={img.url}
                  alt={img.name}
                  className="max-h-full object-contain"
                  onError={() => console.log("‚ùå Î°úÎìú Ïã§Ìå®:", img.url)}
                />
              </div>
              <div className="text-sm text-gray-800 font-semibold">{img.name}</div>
              <div className="text-xs text-gray-500">{formatDate(img.timestamp)}</div>
            </div>
          ))
        )}
      </div>

      {/* Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄ ÏÑπÏÖò */}
      <h2 className="text-xl font-semibold mb-4">üß† AIÍ∞Ä Î∂ÑÏÑùÌïú Ïù¥ÎØ∏ÏßÄ</h2>
      <div className="grid grid-cols-3 gap-6">
        {analyzedImages.length === 0 ? (
          <div className="text-gray-500">Î∂àÎü¨Ïò¨ Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</div>
        ) : (
          analyzedImages.map((img, idx) => (
            <div key={idx} className="border p-2 text-center space-y-2">
              <div className="h-48 flex items-center justify-center border">
                <img
                  src={img.url}
                  alt={img.name}
                  className="max-h-full object-contain"
                  onError={() => console.log("‚ùå Î∂ÑÏÑù Ïù¥ÎØ∏ÏßÄ Î°úÎî© Ïã§Ìå®:", img.url)}
                />
              </div>
              <div className="text-sm text-gray-800 font-semibold">{img.name}</div>
              <div className="text-xs text-gray-500">{formatDate(img.timestamp)}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default latest_image;
